// src/App.js
import React, { useState } from 'react';
import './App.css'; 
import QuizDisplay from './QuizDisplay';
import History from './History';

const BACKEND_URL = 'http://localhost:8000';

function App() {
  const [url, setUrl] = useState('');
  const [quizData, setQuizData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [view, setView] = useState('generator'); // 'generator' or 'history'

  // --- API Handlers ---

  const handleGenerateQuiz = async (e) => {
    e.preventDefault();
    if (!url) {
      setError("Please enter a Wikipedia URL.");
      return;
    }

    setIsLoading(true);
    setError(null);
    setQuizData(null); // Clear previous quiz

    try {
      const response = await fetch(`${BACKEND_URL}/generate_quiz`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || `HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setQuizData(data);
      setUrl(''); // Clear the URL input
    } catch (err) {
      console.error("Fetch Error:", err);
      setError(`Failed to generate quiz: ${err.message}. Check your backend server and Gemini API key.`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFetchQuiz = async (quizId) => {
    setIsLoading(true);
    setError(null);
    setView('generator'); // Switch to generator view to show the result

    try {
      const response = await fetch(`${BACKEND_URL}/quiz/${quizId}`);
      
      if (!response.ok) {
        throw new Error(`Failed to fetch quiz ID ${quizId}.`);
      }

      const data = await response.json();
      setQuizData(data);
    } catch (err) {
      console.error("Fetch Quiz Error:", err);
      setError(`Failed to retrieve quiz: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // --- Main Render ---

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Wiki Quiz Generator</h1>
        <nav>
            <button onClick={() => setView('generator')} className={view === 'generator' ? 'active' : ''}>Generate New Quiz</button>
            <button onClick={() => setView('history')} className={view === 'history' ? 'active' : ''}>Quiz History</button>
        </nav>
      </header>

      <main>
        {view === 'generator' && (
          <div className="generator-container">
            <form onSubmit={handleGenerateQuiz}>
              <input
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="Paste Wikipedia URL here..."
                disabled={isLoading}
                required
              />
              <button type="submit" disabled={isLoading}>
                {isLoading ? 'Generating...' : 'Generate Quiz'}
              </button>
            </form>

            {error && <p className="error-message">{error}</p>}
            
            {/* RENDER THE QUIZ DISPLAY COMPONENT */}
            <QuizDisplay quizData={quizData} />
            
          </div>
        )}
        
        {view === 'history' && (
            <History 
                backendUrl={BACKEND_URL} 
                onSelectQuiz={handleFetchQuiz} 
            />
        )}

      </main>
    </div>
  );
}

export default App;
