// frontend/src/components/QuizDisplay.jsx
import React, { useState, useEffect } from "react";

function QuizQuestion({ questionData, index, handleAnswer, darkMode }) {
  const [selectedOption, setSelectedOption] = useState(null);
  const [showAnswer, setShowAnswer] = useState(false);

  const handleClick = (option) => {
    if (!showAnswer) {
      setSelectedOption(option);
      setShowAnswer(true);
      handleAnswer(option === questionData.answer);
    }
  };

  return (
    <div
      className={`p-3 sm:p-5 md:p-7 lg:p-10 mb-6 rounded-xl shadow transition-all duration-500 ${
        darkMode
          ? "bg-gray-800 border border-gray-700 text-gray-100"
          : "bg-white border border-gray-200 text-gray-800"
      }`}
    >
      <h4 className="text-base sm:text-lg md:text-xl lg:text-2xl font-semibold mb-3">
        {index + 1}. {questionData.question}
      </h4>

      <div className="space-y-2 mb-4">
        {questionData.options.map((option, i) => {
          const isCorrect = option === questionData.answer;
          const isSelected = option === selectedOption;
          return (
            <p
              key={i}
              onClick={() => handleClick(option)}
              className={`p-2 sm:p-3 md:p-4 rounded-md cursor-pointer transition font-medium ${
                showAnswer
                  ? isCorrect
                    ? "bg-green-200 text-green-800 dark:bg-green-700"
                    : isSelected
                    ? "bg-red-200 text-red-800 dark:bg-red-700 line-through"
                    : darkMode
                    ? "bg-gray-700 text-gray-300"
                    : "bg-gray-50 text-gray-500"
                  : darkMode
                  ? "bg-gray-700 hover:bg-gray-600 text-gray-100"
                  : "bg-gray-50 hover:bg-gray-100 text-gray-700"
              }`}
            >
              {option}
            </p>
          );
        })}
      </div>

      {showAnswer && (
        <div
          className={`mt-3 p-3 border-t text-sm sm:text-base ${
            darkMode
              ? "border-blue-700 bg-blue-900 text-gray-200"
              : "border-blue-200 bg-blue-50 text-gray-700"
          }`}
        >
          <p>
            <strong>Correct Answer:</strong>{" "}
            <span className="text-green-500">{questionData.answer}</span>
          </p>
          <p>
            <strong>Difficulty:</strong> {questionData.difficulty}
          </p>
          <p>
            <strong>Explanation:</strong> {questionData.explanation}
          </p>
        </div>
      )}
    </div>
  );
}

export default function QuizDisplay({ quizData }) {
  const [score, setScore] = useState(0);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark") setDarkMode(true);
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  if (!quizData || !quizData.quiz || quizData.quiz.length === 0) return null;

  const { title, summary, key_entities, quiz, related_topics } = quizData;
  const quizList = quiz.slice(0, 10);

  const handleAnswer = (isCorrect) => {
    if (isCorrect) setScore((s) => s + 1);
  };

  return (
    <div
      className={`min-h-screen px-3 sm:px-6 md:px-12 lg:px-20 py-6 transition-colors duration-700 ${
        darkMode ? "bg-gray-900 text-gray-100" : "bg-gray-100 text-gray-900"
      }`}
    >
      {/* Header */}
      <div
        className={`relative p-4 sm:p-6 md:p-8 rounded-lg shadow-lg border-t-4 ${
          darkMode
            ? "bg-gray-800 border-indigo-600"
            : "bg-white border-indigo-500"
        }`}
      >
        <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-extrabold mb-2">
          {title}
        </h2>
        <p className="italic border-b pb-4 mb-4 text-sm sm:text-base md:text-lg">
          <strong>Summary:</strong> {summary}
        </p>
        <div className="text-right font-semibold text-sm sm:text-base md:text-lg">
          Score:{" "}
          <span className="text-indigo-500 font-bold">{score}</span> /{" "}
          {quizList.length}
        </div>

        <button
          onClick={() => setDarkMode(!darkMode)}
          className="absolute top-3 right-3 sm:top-4 sm:right-4 p-2 rounded-full border hover:scale-110 transition"
        >
          {darkMode ? "‚òÄÔ∏è" : "üåô"}
        </button>
      </div>

      {/* Questions */}
      <div
        className={`mt-6 p-3 sm:p-6 md:p-8 rounded-lg shadow-lg ${
          darkMode ? "bg-gray-800" : "bg-gray-50"
        }`}
      >
        <h3 className="text-xl sm:text-2xl md:text-3xl font-bold mb-4">
          Questions ({quizList.length})
        </h3>
        <div className="space-y-4">
          {quizList.map((q, i) => (
            <QuizQuestion
              key={i}
              questionData={q}
              index={i}
              handleAnswer={handleAnswer}
              darkMode={darkMode}
            />
          ))}
        </div>
      </div>

      {/* Metadata */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
        <div
          className={`p-4 sm:p-6 md:p-8 rounded-lg shadow ${
            darkMode ? "bg-gray-800" : "bg-white"
          }`}
        >
          <h4 className="text-lg sm:text-xl md:text-2xl font-bold mb-2">
            Key Entities
          </h4>
          <div className="text-sm sm:text-base md:text-lg space-y-1">
            <p>
              <strong>People:</strong>{" "}
              {key_entities.people.join(", ") || "N/A"}
            </p>
            <p>
              <strong>Organizations:</strong>{" "}
              {key_entities.organizations.join(", ") || "N/A"}
            </p>
            <p>
              <strong>Locations:</strong>{" "}
              {key_entities.locations.join(", ") || "N/A"}
            </p>
          </div>
        </div>

        <div
          className={`p-4 sm:p-6 md:p-8 rounded-lg shadow ${
            darkMode ? "bg-gray-800" : "bg-white"
          }`}
        >
          <h4 className="text-lg sm:text-xl md:text-2xl font-bold mb-2">
            Related Topics
          </h4>
          <ul className="list-disc list-inside text-sm sm:text-base md:text-lg space-y-1">
            {related_topics.map((t, i) => (
              <li key={i}>{t}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
