import React, { useState } from "react";
import GenerateQuizTab from "./tabs/GenerateQuizTab";
// import HistoryTab from "./tabs/HistoryTab"; // Optional

const App = () => {
  const [activeTab, setActiveTab] = useState("generate");

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <div className="flex space-x-4 mb-4">
        <button
          className={`px-4 py-2 rounded-md ${activeTab === "generate" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          onClick={() => setActiveTab("generate")}
        >
          Generate Quiz
        </button>
        <button
          className={`px-4 py-2 rounded-md ${activeTab === "history" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          onClick={() => setActiveTab("history")}
        >
          History
        </button>
      </div>

      {activeTab === "generate" && <GenerateQuizTab />}
      {/* {activeTab === "history" && <HistoryTab />} */}
    </div>
  );
};

export default App;
