export default function QuizDisplay({ quiz }) {
  return (
    <div>
      <h2>Quiz</h2>
      {quiz.quiz.map((q, idx) => (
        <div key={idx} className="card">
          <p><b>Q{idx+1}:</b> {q.question}</p>
          <ul>
            {q.options.map((opt, i) => <li key={i}>{opt}</li>)}
          </ul>
          <p>Answer: {q.answer}</p>
          <p>Explanation: {q.explanation}</p>
          <p>Difficulty: {q.difficulty}</p>
        </div>
      ))}
    </div>
  );
}
